import React, { Component } from "react";
import { MainHeader } from "../components/MainBackground";
import { Link } from "react-router-dom";
import API from "../Utilities/API";
import { Input, FormBtn } from "../components/Form";
import { Row } from "../components/Grid";

class AccountForm extends Component {
  handleFormSubmit = event => {
    event.preventDefault();
    //if (this.state.title && this.state.author) 
    {
      API.saveAccounts({
        email: this.state.email,
        password: this.state.password,
        first_name: this.state.first_name,
        last_name: this.state.last_name,
        child_1_first_name: this.state.child_1_first_name,
        child_2_first_name: this.state.child_2_first_name,
        child_3_first_name: this.state.child_3_first_name,
        elves_2_first_name: this.state.elves_2_first_name,
        elves_3_first_name: this.state.elves_3_first_name,
      })
        .then(res => this.loadAccounts())
        .catch(err => console.log(err));
    }
  };
  render() {
    return (
      <div className="AccountForm">
        <MainHeader>
          <h2>Register for Festival of the Elves</h2>
        </MainHeader>
        <p>
          Please complete the form.
        </p>
        <form>
          <Input
            value={this.state.email}
            onChange={this.handleInputChange}
            name="email"
            placeholder="Email (required)"
          />
          <Input
            value={this.state.password}
            onChange={this.handleInputChange}
            name="password"
            placeholder="Password (required)"
          />
          <Input
            value={this.state.first_name}
            onChange={this.handleInputChange}
            name="first_name"
            placeholder="First Name (required)"
          />
          <Input
            value={this.state.last_name}
            onChange={this.handleInputChange}
            name="last_name"
            placeholder="Last Name (required)"
          />
          <p>
            Please provide your children(s) name(s) to personalize the elf notes.
        </p>
          <Input
            value={this.state.child_1_first_name}
            onChange={this.handleInputChange}
            name="child_1_first_name"
            placeholder="First Child's Name (required)"
          />
          <Input
            value={this.state.child_2_first_name}
            onChange={this.handleInputChange}
            name="child_2_first_name"
            placeholder="Second Child's Name (required)"
          />
          <Input
            value={this.state.child_3_first_name}
            onChange={this.handleInputChange}
            name="child_3_first_name"
            placeholder="Third Child's Name (required)"
          />
         <p>
            Please name your Elves.
        </p>
        <Input
            value={this.state.elves_1_first_name}
            onChange={this.handleInputChange}
            name="elves_1_first_name"
            placeholder="First Elf Name (required)"
          />
          <Input
            value={this.state.elves_2_first_name}
            onChange={this.handleInputChange}
            name="elves_2_first_name"
            placeholder="First Elf Name (required)"
          />
          <FormBtn
            //disabled={!(this.state.author && this.state.title)}
            onClick={this.handleFormSubmit}
          >
            Submit AccountForm
            </FormBtn>
        </form>
        <Row>
          <Link to="/">← Back to Home</Link>
        </Row>

      </div>
    );
  }
}

export default AccountForm;

